CREATE TABLE MAQUINA(
	ID_MAQUINA SERIAL NOT NULL,
	PLACA_MAE VARCHAR(100) NOT NULL,
	ID_MARCA_PROCESSADOR INT NOT NULL,
	PROCESSADOR VARCHAR(100) NOT NULL,
	ID_MODELO_MEMORIA INT NOT NULL,
	ID_FREQUENCIA_MEMORIA INT NOT NULL,
	QUANTIDADE_MEMORIA INT CHECK (QUANTIDADE_MEMORIA > 0) NOT NULL,
	ID_TIPO_ARMAZENAMENTO INT NOT NULL,
	QUANTIDADE_ARMAZENAMENTO INT CHECK (QUANTIDADE_ARMAZENAMENTO > 0) NOT NULL,
	DATA_CADASTRO TIMESTAMP NOT NULL DEFAULT NOW(),
	PRIMARY KEY(ID_MAQUINA),
	FOREIGN KEY(ID_MARCA_PROCESSADOR) REFERENCES TIPO_MARCA_PROCESSADOR(ID_MARCA_PROCESSADOR),
	FOREIGN KEY(ID_MODELO_MEMORIA) REFERENCES TIPO_MODELO_MEMORIA(ID_MODELO_MEMORIA),
	FOREIGN KEY(ID_FREQUENCIA_MEMORIA) REFERENCES TIPO_FREQUENCIA_MEMORIA(ID_FREQUENCIA_MEMORIA),
	FOREIGN KEY(ID_TIPO_ARMAZENAMENTO) REFERENCES TIPO_UNIDADE_ARMAZENAMENTO(ID_TIPO_ARMAZENAMENTO)
);

